import React from 'react'
import BoxInitial from './BoxInitial'
import BoxFinal from './BoxFinal'
import FileSubmitForm from './FileSubmitForm'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

export default function Main() {
  const navigate = useNavigate();

  const handleFileUpload = async (e) => {
    console.log(e);
    e.preventDefault();

    let formData = new FormData();
    formData.append('file', e.target[0].files[0]);

    // axios.post takes an object and sends it to the backend server

    const response = await axios.post(`/handleFile`,
        formData
    ,{
      headers:{
        'content-type': 'multipart/form-data'
      }
    })
    .then((res) => {
      console.log(res);
      if(res.data.status===200){
        navigate('/data');
      }
    })
    .catch(err => console.log(err));


    console.log(response);
  }

  return (
    <>
        <BoxInitial></BoxInitial>
        <BoxFinal></BoxFinal>
        <FileSubmitForm fileSubmitHandler={handleFileUpload}></FileSubmitForm>
    </>
  )
}
