import React,{useState}from 'react'
import BoxInitial from './BoxInitial'
import BoxFinal from './BoxFinal'
import FileSubmitForm from './FileSubmitForm'
import axios from 'axios'

export default function Main() {
  const [image, setImage] = useState({});

  const handleFileUpload = async (e) => {
    console.log(e);
    e.preventDefault();

    let formData = new FormData();
    formData.append('file', image);

    await axios.post(`/handleFile`,{
        formData
    },{
      headers:{
        'Content-Type':'multipart/form-data'
      }
    }).catch(err => console.log(err));
  }

  const handleChange = async (e) => {
   setImage(e.target.files[0]);
  }

  return (
    <div>
        <BoxInitial></BoxInitial>
        <BoxFinal></BoxFinal>
        <FileSubmitForm changeHandler={handleChange} fileSubmitHandler={handleFileUpload}></FileSubmitForm>
    </div>
  )
}
