import React from 'react'
import Button from '../UI/Button'

const FileSubmitForm = React.forwardRef((props,ref) => {
  return (
    <div className={props.className}>
        <form className='bg-white relative' method="POST" onSubmit={props.fileSubmitHandler} encType="multipart/form">
            <input ref={ref} type="file" name="csv-file" className='hidden'></input>
            <Button onClick={buttonClickHandler} type="submit" message="Import a CSV"></Button>;
        </form>
    </div>
  )
})

export default FileSubmitForm;
