import React, { useState } from 'react'


export default function WrapperHome(props) {
  const [isFileOver, setFileOver] = useState(false);
  const [file, setFile] = useState(null);

  const fileOverHandler = (e) => {
    e.preventDefault();
    setFileOver(true);
  }

  const fileLeaveHandler =  (e) => {
    e.preventDefault();

    setFileOver(false);
  }

  const fileDropHandler = (e) => {
    e.preventDefault();
    setFile(e.dataTransfer.files[0]);
    console.log(e);
  }

  // For drop to work as per your expectations, please dropOver ko preventDefault karo.

  return (
    <div onDrop={fileDropHandler} file={file} onDragOver={fileOverHandler} onDragLeave={fileLeaveHandler} className={`border-black w-full outline-dashed h-screen ${props.className} ${isFileOver && 'bg-slate-400'}`}>
        {props.children}
    </div>
  )
}
