import React,{useState, useEffect} from 'react'
import FileSubmitForm from './FileSubmitForm'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import WrapperHome from '../UI/WrapperHome'

export default function Main() {
  const[dataReceived, setDataReceived] = useState(false);
  const [isFileOver, setFileOver] = useState(false);
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  useEffect(()=>{
    if(dataReceived){
      navigate('/data',{replace:true})
    }
  },[dataReceived])

  const fileOverHandler = (e) => {
    e.preventDefault();
    setFileOver(true);
  }

  const fileLeaveHandler =  (e) => {
    e.preventDefault();

    setFileOver(false);
  }

  const fileDropHandler = async (e) => {
    e.preventDefault();
    console.log();
    setFile(e.dataTransfer.files[0]);

    let formData = new FormData();
    formData.append('file', file);

    // axios.post takes an object and sends it to the backend server

    const response = await axios.post(`/handleFile`,
        formData
    ,{
      headers:{
        'content-type': 'multipart/form-data'
      }
    })

    if(response.data.status===200){
      setDataReceived(true); 
    }

    console.log(e);
  }

  return (
    <WrapperHome className={isFileOver && 'bg-slate-400'} fileDropHandler={fileDropHandler} fileLeaveHandler={fileLeaveHandler} fileOverHandler={fileOverHandler}>
      <div className=' relative top-1/2 -translate-y-1/2 '>
        <FileSubmitForm></FileSubmitForm>
      </div>
    </WrapperHome>
  )
}
