import React, { useState } from 'react'


export default function WrapperHome(props) {
  const [isFileOver, setFileOver] = useState(false);

  const fileOverHandler = (e) => {
    setFileOver(true);
  }

  const fileLeaveHandler =  (e) => {
    e.preventDefault();

    setFileOver(false);
  }

  const dropFileHandler = (e) => {
    e.preventDefault();
    console.log(e);
  }
  return (
    <div onDrop={dropFileHandler} onDragOver={fileOverHandler} onDragLeave={fileLeaveHandler} className={`border-black w-full outline-dashed h-screen ${props.className} ${isFileOver && 'bg-slate-400'}`}>
        {props.children}
    </div>
  )
}
