import React, { useState } from 'react'
import sort from '../styles/sort.svg'

export default function TableHeaderBox(props) {
  const [order, setOrder] = useState(false);

  const sortHandler = (e) => {
    setOrder(order => !order);
    props.sortHandler(e);
  }

  return (
    <>
        <p className='text-center'>{props.headerName}<img alt='sort' src={sort} order={`$order`} id={props.headerName} onClick={sortHandler} className='inline cursor-pointer'></img></p>
    </>
  )
}
