import React,{useState, useEffect} from 'react'
import FileSubmitForm from './FileSubmitForm'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import WrapperHome from '../UI/WrapperHome'

export default function Main() {
  const[dataReceived, setDataReceived] = useState(false);
  const [isFileOver, setFileOver] = useState(false);
  const navigate = useNavigate();

  useEffect(()=>{
    if(dataReceived){
      navigate('/data',{replace:true})
    }
  },[dataReceived, navigate])

  const fileOverHandler = (e) => {
    e.preventDefault();
    setFileOver(true);
  }

  const fileLeaveHandler =  (e) => {
    e.preventDefault();

    setFileOver(false);
  }

  const fileDropHandler = async (e) => {
    e.preventDefault();

    let formData = new FormData();
    formData.append('file', e.dataTransfer.files[0]);

    // axios.post takes an object and sends it to the backend server

    const response = await axios.post(`/handleFile`,
        formData
    ,{
      headers:{
        'content-type': 'multipart/form-data'
      }
    })

    if(response.data.status===200){
      setDataReceived(true); 
    }

    setFileOver(false);

  }

  const handleFileUpload = async (e) => {
    e.preventDefault();

    let formData = new FormData();
    formData.append('file', e.target[0].files[0]);


    // axios.post takes an object and sends it to the backend server

    const response = await axios.post(`/handleFile`,
        formData
    ,{
      headers:{
        'content-type': 'multipart/form-data'
      }
    })

    if(response.data.status===200){
      setDataReceived(true); 
    }

  }

  return (
      <WrapperHome className={`w- h-52 m-auto relative top-1/2 -translate-y-1/2   ${isFileOver && 'bg-slate-400'}`} fileDropHandler={fileDropHandler} fileLeaveHandler={fileLeaveHandler} fileOverHandler={fileOverHandler}>
        <div className="">
          <FileSubmitForm fileSubmitHandler={handleFileUpload}></FileSubmitForm>
        </div>
      </WrapperHome>
  )
}
